mapper.insert:
insert into noti(type, cno, fwer_no, bno, uno)
values(#{type}, #{comment.no}, #{follower.no}, #{badge.no},


		#{userNo}



		(select a.writer from comment c inner join article a on a.arno = c.arno where c.cno = #{comment.no})


